import React, { Component } from 'react';
import Alert from 'react-bootstrap/Alert';
import { ThemeConsumer } from 'react-bootstrap/esm/ThemeProvider';

class FormTemplate extends Component {

    state = { 
        fields: this.props.fields,
        validationError: false
     };

    handleChange(e) {
        const { fields } = this.state;
        const name = e.target.name, newValue = e.target.value;
        const oldField = fields[name];
        const updatedField = {...oldField, value: newValue};

        this.setState({fields: {...fields, [name]: updatedField}});
    }

    handleSubmit(e) {
        e.preventDefault();
        const { fields } = this.state, { isValid, submit } = this.props;

        if(isValid(fields))
            submit(this.state.fields);
        else
            this.setState({validationError: true});
    }

    render() { 

        const { fields, validationError } = this.state, formFields = [];

        for (let name in fields) {
            formFields.push({
                name,
                ...fields[name]
            })
        }

        return(
            <form onSubmit={e => this.handleSubmit(e)} className="content container bordered has-background-white">

                {formFields.map(field => (
                    <div style={{padding: "10px 0"}}>
                        <label>{field.displayName}</label>

                        <input 
                            required
                            className="input"
                            type={field.type} 
                            name={field.name} 
                            value={field.value}
                            onChange={e => this.handleChange(e)} 
                            placeholder={field.displayName}
                        />
                    </div>
                ))}

                {validationError && <Alert variant="danger">{this.props.validationErrorMessage}</Alert>}

                <button className="button" type="submit">Submit</button>
            </form>
        );

    }
}

export default FormTemplate;
