import React, { useState } from 'react';
import Question from './question';
import NewQuestion from './newQuestion';

const QuizCreationForm = () => {

    const [topic, setTopic] = useState("");
    const [questions, setQuestions] = useState([]);
    const [addingQuestion, setAddingQuestion] = useState(false);

    const addQuestion = () => {
        setAddingQuestion(true);
    }

    const submitQuestion = newQuestion => {
        console.log(newQuestion)
        questions.push(newQuestion);
        setAddingQuestion(false);
    }

    return(
        <div>

            <h2>{topic}</h2>
            {questions.map((question, idx) => <Question question={question} number={idx} />)}

            {addingQuestion ? 
                <NewQuestion number={questions.length+1} submitQuestion={submitQuestion} />
            :
                <button className="button is-info" onClick={addQuestion}>Add New Question</button>}

        </div>
    )
}

export default QuizCreationForm;
