import React from 'react';
import Question from './question';

const Quiz = ({ quiz, checkAnswers }) => {

    const { topic, questions } = quiz;



    return(
        <div className="container" style={{padding: "3% 0", maxWidth: "600px"}}>
            <h3>{topic}</h3>

            {questions && questions.map((question, idx) => <Question key={idx} number={idx+1} question={question} />)}

            <button onClick={checkAnswers} className="button is-info">Check Answers</button>
        </div>
    )
};

export default Quiz;
