import React, { Component } from 'react';
import AnswerChoice from './answerChoice';
import './styles.css';

class  NewQuestion extends Comopnent {

    state = {
        questionPrompt: "",
        answerChoices: ["", "", "", ""]
    }

    remove = deletionIdx => {

        const remainingAnswers = answerChoices.filter((_, idx) => idx !== deletionIdx);

        if(remainingAnswers.length < 2)
            alert("There must be more than one answer choice.")
        else
            this.setState({remainingAnswers});
    }

    render() {

        const { questionPrompt, answerChoices } = this.state;

        return(
            <div className="new-question">
                <input 
                    className="new-question-prompt" 
                    type="text" 
                    placeholder="Add your question" 
                />

                {answerChoices.map((answerChoice, idx) => (

                        <AnswerChoice 
                            value={answerChoice} 
                            idx={idx} 
                            remove={remove} 
                        />
                ))}

            </div>
        )
    }
}

export default NewQuestion;
