import React from 'react';
import 'components/create/styles.css';
import './styles.css';

const Question = ({ question, number }) => {

    const { prompt, answerChoices, correctAnswer, id } = question;

    return(
        <div className="new-question">
            <h4>Question {number}</h4>

            <h6 className="quiz-question-prompt">{prompt}</h6>

            {answerChoices && answerChoices.map((answerChoice, idx) => {

                return(
                    <div key={idx} className="quiz-question-answer">
                        <input type="radio" name={`question ${id}`}/>

                        <div className="box" style={{padding: ".75rem", margin: "5px 0", width: "500px"}}>
                            {answerChoice.body}
                        </div>
                    </div>
                )

            })}
        </div>
    )
}

export default Question;
