import { apiHost } from 'config';
import axios from 'axios';

const putToServer = (path, body) => {

    return axios.put(`${apiHost}/${path}`, body, {
        headers: {
            'Authorization': `Bearer ${localStorage.getItem('jwt')}`
        }
    })
};

export default putToServer;
