import { apiHost } from 'config';
import axios from 'axios';

const updateQuiz = quiz => {
    return axios.put(`${apiHost}/quizzes/${quiz.id}`, body, {
        headers: {
            'Authorization': `Bearer ${localStorage.getItem('jwt')}`
        }
    })
};

export default updateQuiz;
