import React from 'react';
import GradedQuestion from './gradedQuestion';
import runGrader from 'utils/runGrader';

const GradedQuiz = ({ quiz, givenAnswers }) => {

    const { topic, questions, creator } = quiz, gradeMessage = runGrader(quiz.questions, givenAnswers);

    return(
        <div className="container" style={{padding: "3% 0", maxWidth: "600px"}}>

            <h3>Quiz on {topic} by {creator}</h3>
            <div className="notification is-info">{gradeMessage}</div>

            {questions && questions.map((question, idx) => {

                const chosenAnswer = givenAnswers[question.id];

                return(
                    <GradedQuestion 
                        key={idx} 
                        number={idx+1} 
                        question={question} 
                        chosenAnswer={chosenAnswer}
                    />
                )
            })}
        </div>
    )
};

export default GradedQuiz;
