import React, { Component } from 'react';

class Register extends Component {

    state = {
        fields: [
            {name: "firstName", displayName: "First Name", type: "text", value: ""},
            {name: "lastName", displayName: "Last Name", type: "text", value: ""},
            {name: "email", displayName: "Email", type: "email", value: ""},
            {name: "password", displayName: "Password", type: "password", value: ""},
            {name: "passwordConfirmation", displayName: "Confirm Password", type: "password", value: ""}
        ]
    };

    handleChange(e) {
        const name = e.target.name, value = e.target.value;
        console.log(name, value);
    }

    render() {

        const { fields } = this.state;

        return(
            <form onSubmit={this.register} className="content container">
                {fields.map(field => (
                    <div>
                        <label>{field.displayName}</label>

                        <input 
                            className="input"
                            type={field.type} 
                            name={field.name} 
                            value={field.value}
                            onChange={this.handleChange} 
                            placeholder={field.displayName}
                        />
                    </div>
                ))}

                <button className="button" type="submit">Submit</button>
            </form>
        );
    }
};

export default Register;
