import React, { useState } from 'react';
import AnswerChoice from './answerChoice';
import './styles.css';

const NewQuestion = () => {

    const [answerChoices, setAnswerChoices] = useState(["", "", "", ""]);

    const remove = deletionIdx => {
        const remainingAnswers = answerChoices.filter((_, idx) => idx !== deletionIdx);

        if(remainingAnswers.length < 2)
            alert("There must be more than one answer choice.")
        else
            setAnswerChoices(remainingAnswers);
    }

    return(
        <div className="new-question">
            <input className="new-question-prompt" type="text" placeholder="Add your question" />

            {answerChoices.map((answerChoice, idx) => {
                return 
                    <AnswerChoice 
                        value={answerChoice} idx={idx} remove={remove} />
            })}

        </div>
    )
}

export default NewQuestion;
