import React, { useState } from 'react';
import AnswerChoice from './answerChoice';
import './styles.css';

const NewQuestion = () => {

    const [answerChoices, setAnswerChoices] = useState([
        {}
    ]);

    const remove = id => {
        answerChoices.find(answerChoice => answerChoice)
    }

    return(
        <div className="new-question">
            <input className="new-question-prompt" type="text" placeholder="Add your question" />

            {answerChoices.map((answerChoice, id) => <AnswerChoice value={answerChoice} remove={remove} />)}
        </div>
    )
}

export default NewQuestion;
